class Orders extends Admin_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Orders_model');
    } 

    /*
     * Listing of customers
     */
    function index()
    {

        $data['orders'] = $this->Orders_model->get_all_Order();
      // echo "<pre>"; print_r($data['orders']);exit();
          

          

        
        $data['_view'] = 'orders/index';
        $this->load->view('layouts/main',$data);
    }

     function remove($id)
    {
        $order_item = $this->Orders_model->get_order($id);

        // check if the admin exists before trying to delete it
        if(isset($order_item['id']))
        {
            $this->Orders_model->delete_order($id);
            redirect('Orders/index');
        }
        else
            show_error('The Order you are trying to delete does not exist.');
    }
}